SELECT t.item_code itemCode, t.item_name itemName, t.item_spec itemSpec, t.remark projectId, SUM( IFNULL( t.osd_amount_plan, 0 ) ) osdAmountPlan, SUM( IFNULL( t.osd_amount_real, 0 ) ) osdAmountReal, SUM( IFNULL( t.osd_amount_plan, 0 ) ) osdAmountConsumption, SUM( IFNULL( t.osd_amount_real, 0 )- IFNULL( t.osd_amount_plan, 0 )) osdAmountRetired FROM sms_wms_out_stock_detail t WHERE doc_no IN
#{item}
GROUP BY t.item_code, t.item_name
